package Com.pac;

public class Loop_Selection {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int z = 21;
		
		do {
			System.out.println(z);
			z--;
			break;
			//continue;
		}while (z >=0);
		System.out.println();
	
		do {
			System.out.println(z);
			z--;
			
			
			//continue;
		}while (z >=0);
		System.out.println();
		
		do {
			System.out.println(z);
			z--;
			
			//continue;
		}while (z >=0);
		System.out.println();

		do {
			System.out.println(z);
			z--;
			
			//continue;
		}while (z >=0);
		System.out.println();
	}}
